import { HttpClient } from '@angular/common/http';
import { inject, Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { FaceSnap } from '../models/face-snap';
import { SnapType } from './../models/snap-type.type';

@Injectable({
  providedIn: 'root',
})
export class FaceSnapsService {
  // constructor(private httpClient: HttpClient) {}
  private httpClient = inject(HttpClient);

  getAllFaceSnaps(): Observable<FaceSnap[]> {
    return this.httpClient.get<FaceSnap[]>('http://localhost:3000/facesnaps');
  }
  private faceSnaps: FaceSnap[] = [
    new FaceSnap(
      1,
      'Claude',
      'https://cdn.pixabay.com/photo/2015/05/31/16/03/teddy-bear-792273_1280.jpg',
      'Mon meilleur ami depuis deux mois bientot',
      new Date(),
      15000
    ),

    new FaceSnap(
      2,
      'Mon Delicieux Repas',
      'data:image/jpeg;base64,',
      'J aime bien ',
      new Date(),
      1000
    ).withLocation('Au restaurant'),

    new FaceSnap(
      3,
      'Ma petite-fille',
      'data:image/jpeg;base64,',
      'Elle a été malade depuis une semaine',
      new Date(),
      50
    ),
  ];

  getFaceSnaps(): Observable<FaceSnap[]> {
    const result = this.httpClient.get<FaceSnap[]>('http://localhost:3000/facesnaps');
    return result;
  }

  getFaceSnapById(faceSnapId: number): FaceSnap {
    console.log(this.faceSnaps, faceSnapId);
    const foundFaceSnap = this.faceSnaps.find(
      (faceSnap) => faceSnap.id === +faceSnapId
    );
    if (!foundFaceSnap) {
      throw new Error('FaceSnap not found!');
    }
    return foundFaceSnap;
  }

  snapFaceSnapById(faceSnapId: number, snapType: SnapType): void {
    const faceSnap: FaceSnap = this.getFaceSnapById(faceSnapId);
    faceSnap.snap(snapType);
  }

  addFaceSnap(formValue: {
    title: string;
    description: string;
    imageUrl: string;
    location?: string;
  }) {
    const faceSnap: any = {
      ...formValue,
      snaps: 0,
      createdDate: new Date(),
      id: this.faceSnaps[this.faceSnaps.length - 1].id + 1,
    };
    this.faceSnaps.push(faceSnap);
  }
}
